// src/context/AppContext.js
import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [budget, setBudget] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");


  const token = localStorage.getItem("token");
  // Charger les données au démarrage


  // Charger les transactions
  useEffect(() => {
    fetch("http://localhost:5000/transactions/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        const sanitized = (data.transactions || []).map((t) => ({
          ...t,
          categories: Array.isArray(t.categories) ? t.categories : [],
        }));
        setTransactions(sanitized);
      })
      .catch((err) => console.error("Erreur lors du chargement des transactions :", err));
  }, [token]);

    useEffect(() => {
    fetch("http://localhost:5000/categories/")
      .then((res) => {
        if (!res.ok) throw new Error("Erreur lors du chargement des catégories");
        return res.json();
      })
      .then((data) => {
        // Le backend peut renvoyer {"categories": [...]} ou un tableau direct
        setCategories(data.categories || data);
        console.log("Catégories chargées :", data);
      })
      .catch((err) => {
        console.error("Erreur lors du chargement :", err);
        setErrorMessage("Impossible de charger les catégories.");
      });
  }, []);




  const fetchData = async () => {
    try {
          //const transRes = await axios.get("http://127.0.0.1:5000/transactions", {
          //headers: {
            //Authorization: `Bearer ${token}`,
          //}, });
      //const catRes = await axios.get("http://127.0.0.1:5000/categories");
      const budgetRes = await axios.get("http://127.0.0.1:5000/budget");

      //setTransactions(transRes.data);
      //setCategories(catRes.data);
      setBudget(budgetRes.data.totalBudget);
    } catch (error) {
      setErrorMessage("Erreur lors du chargement des données.");
      console.error(error);
    }
  };


  const addTransaction = async (e) => {
    e.preventDefault();
    const payload = {
      amount: parseFloat(newTransaction.amount),
      type: newTransaction.type,
      description: newTransaction.description,
      date: newTransaction.date || null,
      category_ids: newTransaction.category_ids,
    };

    try {
      const response = await fetch("http://localhost:5000/transactions/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });
      const data = await response.json();

      if (response.ok) {
        const t = {
          ...data.transaction,
          categories: Array.isArray(data.transaction.categories) ? data.transaction.categories : [],
        };
        setTrans((prev) => [...prev, t]);
        setNewTransaction({
          description: "",
          amount: "",
          type: "",
          category_ids: [],
          date: "",
        });
      } else {
        console.error("Erreur serveur :", data.message);
      }
    } catch (err) {
      console.error("Erreur fetch :", err);
    }
  };

  // Supprimer une transaction
  const deleteTransaction = async (id) => {
    try {
      await axios.delete(`/api/transactions/${id}`);
      setTransactions(transactions.filter((t) => t.id !== id));
      setSuccessMessage("Transaction supprimée !");
    } catch (error) {
      setErrorMessage("Impossible de supprimer la transaction.");
      console.error(error);
    }
  };

  return (
    <AppContext.Provider
      value={{
        transactions,
        categories,
        budget,
        errorMessage,
        successMessage,
        setErrorMessage,
        setSuccessMessage,
        addTransaction,
        deleteTransaction,
        fetchData,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};
