// src/pages/Categories.jsx
import React, { useContext, useState,useEffect } from "react";
import { AppContext } from "../context/AppContext";

const Categories = () => {
  const {errorMessage, successMessage, setErrorMessage, setSuccessMessage } = useContext(AppContext);
  const [newCategory, setNewCategory] = useState("");
  const [categories,setCategories]=useState([]);

  const handleAdd = () => {
    if (!newCategory) {
      setErrorMessage("Nom de catégorie requis");
      return;
    }

    console.log(newCategory)

    fetch("http://localhost:5000/categories", {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },
    body: JSON.stringify(newCategory)
  })
    .then((res) => res.json())
    .then((data) => {
      setNewCategory(data);
      setSuccessMessage(`Catégorie ${newCategory} ajoutée !`);
      setNewCategory("");
    })
    .catch((err) => {
      console.error("Erreur :", err);
    });
  };

    useEffect(() => {
      fetch("http://localhost:5000/categories")
        .then((res) => res.json())
        .then((data) => {
          setCategories(data);
          console.log("Categories chargées :", data);
        })
        .catch((err) => console.error("Erreur lors du chargement :", err));
    }, []);

  return (
    <div className="categories-page">
      <h1>Catégories</h1>

      {errorMessage && <p className="error">{errorMessage}</p>}
      {successMessage && <p className="success">{successMessage}</p>}

      <input type="text" placeholder="Nouvelle catégorie" value={newCategory} onChange={(e) => setNewCategory(e.target.value)} />
      <button onClick={handleAdd} style={{ backgroundColor: "#90EE90" }}>Ajouter</button>

      <ul>
        {categories.map((cat) => (
          <li key={cat.id}>{cat.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Categories;
