import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [token, setToken] = useState(() => localStorage.getItem("token"));
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [budget, setBudget] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [newTransaction, setNewTransaction] = useState({
    description: "",
    amount: "",
    type: "",
    category_ids: [],
    date: "",
  });

  // üîÅ Synchroniser automatiquement le token avec le localStorage
  useEffect(() => {
    if (token) localStorage.setItem("token", token);
    else localStorage.removeItem("token");
  }, [token]);

  // ‚úÖ Charger les transactions d√®s que le token est pr√™t
  useEffect(() => {
    if (!token) {
      console.warn("Aucun token trouv√©, transactions non charg√©es.");
      return;
    }

    fetch("http://localhost:5000/transactions/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erreur d'autorisation ou serveur.");
        return res.json();
      })
      .then((data) => {
        const sanitized = (data.transactions || []).map((t) => ({
          ...t,
          categories: Array.isArray(t.categories) ? t.categories : [],
        }));
        setTransactions(sanitized);
      })
      .catch((err) =>
        console.error("Erreur lors du chargement des transactions :", err)
      );
  }, [token]);

  // ‚úÖ Charger les cat√©gories (ne d√©pend pas du token)
  useEffect(() => {
    fetch("http://localhost:5000/categories/")
      .then((res) => {
        if (!res.ok) throw new Error("Erreur lors du chargement des cat√©gories");
        return res.json();
      })
      .then((data) => {
        setCategories(data.categories || data);
      })
      .catch((err) => {
        console.error("Erreur lors du chargement des cat√©gories :", err);
        setErrorMessage("Impossible de charger les cat√©gories.");
      });
  }, []);

  // ‚úÖ Ajouter une transaction
  const addTransaction = async (e) => {
    e.preventDefault();
    if (!token) {
      setErrorMessage("Non autoris√©. Veuillez vous reconnecter.");
      return;
    }

    const payload = {
      amount: parseFloat(newTransaction.amount),
      type: newTransaction.type,
      description: newTransaction.description,
      date: newTransaction.date || null,
      category_ids: newTransaction.category_ids,
    };

    try {
      const response = await fetch("http://localhost:5000/transactions/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (response.ok) {
        const t = {
          ...data.transaction,
          categories: Array.isArray(data.transaction.categories)
            ? data.transaction.categories
            : [],
        };
        setTransactions((prev) => [...prev, t]);
        setNewTransaction({
          description: "",
          amount: "",
          type: "",
          category_ids: [],
          date: "",
        });
      } else {
        console.error("Erreur serveur :", data.message);
      }
    } catch (err) {
      console.error("Erreur lors de l'ajout de la transaction :", err);
    }
  };

  // ‚úÖ Supprimer une transaction
  const deleteTransaction = async (id) => {
    if (!token) return console.error("Token manquant pour suppression.");

    try {
      await axios.delete(`http://localhost:5000/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions((prev) => prev.filter((t) => t.id !== id));
      setSuccessMessage("Transaction supprim√©e !");
    } catch (error) {
      setErrorMessage("Impossible de supprimer la transaction.");
      console.error(error);
    }
  };

  return (
    <AppContext.Provider
      value={{
        token,
        setToken, // ‚úÖ pour Login et Logout
        transactions,
        setTransactions,
        categories,
        budget,
        errorMessage,
        successMessage,
        setErrorMessage,
        setSuccessMessage,
        addTransaction,
        deleteTransaction,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};
