import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "../context/AppContext";
import axios from "axios";
import "../App.css";

function Transactions() {
  const {
    transactions,
    setTransactions,
    categories,
    setCategories,
    token,
  } = useContext(AppContext);

  const [newTransaction, setNewTransaction] = useState({
    description: "",
    amount: "",
    type: "",
    category_ids: [],
    date: "",
  });

  // ✅ Charger les transactions si la page est rafraîchie
  useEffect(() => {
    if (!token) {
      console.warn("Aucun token trouvé, impossible de charger les transactions.");
      return;
    }

    const fetchTransactions = async () => {
      try {
        const res = await axios.get("http://localhost:5000/transactions/", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = res.data.transactions || [];
        const sanitized = data.map((t) => ({
          ...t,
          categories: Array.isArray(t.categories) ? t.categories : [],
        }));
        setTransactions(sanitized);
      } catch (err) {
        console.error("Erreur lors du chargement des transactions :", err);
      }
    };

    // Charger les catégories aussi
    const fetchCategories = async () => {
      try {
        const res = await axios.get("http://localhost:5000/categories/");
        setCategories(res.data.categories || res.data);
      } catch (err) {
        console.error("Erreur lors du chargement des catégories :", err);
      }
    };

    fetchTransactions();
    fetchCategories();
  }, [token, setTransactions, setCategories]);

  // ✅ Gérer les changements de formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: name === "amount" ? parseFloat(value) : value,
    }));
  };

  // ✅ Gérer le changement de catégorie
  const handleCategoryChange = (e) => {
    const id = parseInt(e.target.value);
    setNewTransaction((prev) => ({
      ...prev,
      category_ids: id ? [id] : [],
    }));
  };

  // ✅ Ajouter une transaction
  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      amount: parseFloat(newTransaction.amount),
      type: newTransaction.type,
      description: newTransaction.description,
      date: newTransaction.date || null,
      category_ids: newTransaction.category_ids,
    };

    try {
      const response = await axios.post(
        "http://localhost:5000/transactions/",
        payload,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      const data = response.data.transaction;
      const t = {
        ...data,
        categories: Array.isArray(data.categories) ? data.categories : [],
      };

      setTransactions((prev) => [...prev, t]);
      setNewTransaction({
        description: "",
        amount: "",
        type: "",
        category_ids: [],
        date: "",
      });
    } catch (err) {
      console.error("Erreur lors de l’ajout de la transaction :", err);
    }
  };

  // ✅ Supprimer une transaction
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions((prev) => prev.filter((t) => t.id !== id));
    } catch (err) {
      console.error("Erreur lors de la suppression :", err);
    }
  };

  return (
    <div className="page-container">
      <h1>Transactions</h1>

      {/* Formulaire d’ajout */}
      <form onSubmit={handleSubmit} className="form-section">
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTransaction.description}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="amount"
          placeholder="Montant"
          min="0"
          step="0.01"
          value={newTransaction.amount}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="type"
          placeholder="Type (revenu / dépense)"
          value={newTransaction.type}
          onChange={handleChange}
          required
        />
        <select
          name="category"
          value={newTransaction.category_ids[0] || ""}
          onChange={handleCategoryChange}
        >
          <option value="">-- Choisir une catégorie --</option>
          {categories.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          name="date"
          value={newTransaction.date}
          onChange={handleChange}
          required
        />
        <button type="submit">Ajouter</button>
      </form>

      {/* Liste des transactions */}
      <div className="list-section">
        {transactions.length === 0 ? (
          <p>Aucune transaction enregistrée.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Description</th>
                <th>Montant (HTG)</th>
                <th>Catégories</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((t) => (
                <tr key={t.id}>
                  <td>{t.description}</td>
                  <td>{t.amount}</td>
                  <td>{t.categories.map((c) => c.name).join(", ") || "Aucune"}</td>
                  <td>{t.date ? new Date(t.date).toLocaleDateString() : "—"}</td>
                  <td>
                    <button
                      onClick={() => handleDelete(t.id)}
                      className="delete-btn"
                    >
                      Supprimer
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Transactions;
