import React, { useContext, useState } from "react";
import { AppContext } from "../context/AppContext";
import axios from "axios";
import "./Categories.css";

const Categories = () => {
  const {
    categories,
    setCategories,
    token,
    errorMessage,
    successMessage,
    setErrorMessage,
    setSuccessMessage,
  } = useContext(AppContext);

  const [newCategory, setNewCategory] = useState("");

  const handleAdd = async () => {
    if (!newCategory) {
      setErrorMessage("Nom de catégorie requis");
      return;
    }

    if (!token) {
      setErrorMessage("Token manquant, veuillez vous reconnecter.");
      return;
    }

    try {
      // Appel à l'API pour ajouter la catégorie
      const response = await axios.post(
        "http://localhost:5000/categories/",
        { name: newCategory },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.status === 201 || response.status === 200) {
        const addedCategory = response.data.category;

        // Mettre à jour le state local immédiatement
        setCategories((prev) => [...prev, addedCategory]);
        setSuccessMessage(`Catégorie "${newCategory}" ajoutée !`);
        setNewCategory("");
      } else {
        setErrorMessage("Erreur lors de l'ajout de la catégorie.");
      }
    } catch (err) {
      console.error(err);
      setErrorMessage("Erreur serveur lors de l'ajout de la catégorie.");
    }
  };

  return (
    <div className="categories-page">
      <h1>Catégories</h1>

      {errorMessage && <p className="error">{errorMessage}</p>}
      {successMessage && <p className="success">{successMessage}</p>}

      <div className="add-category">
        <input
          type="text"
          placeholder="Nouvelle catégorie"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        />
        <button onClick={handleAdd} style={{ backgroundColor: "#90EE90" }}>
          Ajouter
        </button>
      </div>

      <ul>
        {categories.map((cat) => (
          <li key={cat.id}>{cat.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Categories;
