import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "../context/AppContext";
import axios from "axios";
import "../App.css";

function Transactions() {
  const { transactions, setTransactions } = useContext(AppContext);
  const [categories, setCategories] = useContext(AppContext);
  const [newTransaction, setNewTransaction] = useState({
    description: "",
    amount: "",
    category: "",
    date: "",
  });

  // Gérer le formulaire d’ajout
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: name === "amount" ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const transactionToSend = {
      ...newTransaction,
      amount: parseFloat(newTransaction.amount), // ✅ conversion sécurisée
    };

    axios
      .post("http://localhost:5000/api/transactions", transactionToSend)
      .then((res) => {
        setTransactions([...transactions, res.data]);
        setNewTransaction({ description: "", amount: "", category: "", date: "" });
      })
      .catch((err) => console.error("Erreur lors de l’ajout :", err));
  };


    const handleCategoryChange = (e) => {
    const id = parseInt(e.target.value);
    setNewTransaction((prev) => ({
      ...prev,
      category_ids: id ? [id] : [],
    }));
  };


 return (
    <div className="page-container">
      <h1>Transactions</h1>

      {/* Formulaire d’ajout */}
      <form onSubmit={handleSubmit} className="form-section">
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTransaction.description}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="amount"
          placeholder="Montant"
          min="0"
          step="0.01"
          value={newTransaction.amount}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="type"
          placeholder="Type (revenu / dépense)"
          value={newTransaction.type}
          onChange={handleChange}
          required
        />
        <select
          name="category"
          value={newTransaction.category_ids[0] || ""}
          onChange={handleCategoryChange}
        >
          <option value="">-- Choisir une catégorie --</option>
          {categories.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          name="date"
          value={newTransaction.date}
          onChange={handleChange}
          required
        />
        <button type="submit">Ajouter</button>
      </form>

      {/* Liste des transactions */}
      <div className="list-section">
        {transactions.length === 0 ? (
          <p>Aucune transaction enregistrée.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Description</th>
                <th>Montant (HTG)</th>
                <th>Catégories</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((t) => (
                <tr key={t.id}>
                  <td>{t.description}</td>
                  <td>{t.amount}</td>
                  <td>{t.categories.map((c) => c.name).join(", ") || "Aucune"}</td>
                  <td>{t.date ? new Date(t.date).toLocaleDateString() : "—"}</td>
                  <td>
                    <button onClick={() => handleDelete(t.id)} className="delete-btn">
                      Supprimer
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Transactions;
