import React, { useContext, useMemo } from "react";
import { AppContext } from "../context/AppContext";

const Budget = () => {
  // üîπ R√©cup√©ration du budget et des transactions depuis le contexte global
  const { budget, transactions } = useContext(AppContext);

  // üîπ Calcul des d√©penses totales
  const totalSpent = useMemo(
    () => transactions.reduce((sum, t) => sum + Number(t.amount || 0), 0),
    [transactions]
  );

  // üîπ Calcul du solde restant
  const remaining = budget - totalSpent;

  return (
    <div
      className="budget-page"
      style={{
        maxWidth: "500px",
        margin: "40px auto",
        backgroundColor: "#f8f9fa",
        borderRadius: "10px",
        padding: "25px",
        boxShadow: "0px 3px 6px rgba(0,0,0,0.1)",
      }}
    >
      <h1 style={{ textAlign: "center", color: "#00796b" }}>üí∞ Budget FinansPam</h1>

      <div style={{ marginTop: "25px", lineHeight: "1.8" }}>
        <p>
          <strong>Budget total :</strong> {budget.toLocaleString()} HTG
        </p>
        <p>
          <strong>Total des d√©penses :</strong> {totalSpent.toLocaleString()} HTG
        </p>
        <p>
          <strong>Solde restant :</strong>{" "}
          <span style={{ color: remaining < 0 ? "red" : "green" }}>
            {remaining.toLocaleString()} HTG
          </span>
        </p>

        {remaining < 0 && (
          <p
            style={{
              color: "red",
              fontWeight: "bold",
              marginTop: "15px",
            }}
          >
            ‚ö†Ô∏è Attention : vous avez d√©pass√© votre budget !
          </p>
        )}
      </div>
    </div>
  );
};

export default Budget;
