import React, { useContext, useEffect, useState } from "react";
import { AppContext } from "../context/AppContext";
import { Link } from "react-router-dom";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
import { Doughnut } from "react-chartjs-2";
import "../App.css";
import "./Dashboard.css";

ChartJS.register(ArcElement, Tooltip, Legend);

const Dashboard = () => {
  const {
    transactions,
    categories,
    token,
    setToken, // pour le logout
  } = useContext(AppContext);

  const [recentTransactions, setRecentTransactions] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredTransactions, setFilteredTransactions] = useState([]);

  // ✅ Utilisation du contexte (et plus du localStorage)
  useEffect(() => {
    if (transactions && token) {
      const sorted = [...transactions].sort(
        (a, b) => new Date(b.date) - new Date(a.date)
      );
      const latest = sorted.slice(0, 3);
      setRecentTransactions(latest);
      setFilteredTransactions(latest);
    }
  }, [transactions, token]);

  const handleSearch = () => {
    const filtered = recentTransactions.filter((t) =>
      t.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredTransactions(filtered);
  };

  const handleLogout = async () => {
    try {
      const response = await fetch("http://localhost:5000/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setToken(null); // ✅ supprime le token du contexte
        localStorage.removeItem("token");
        window.location.href = "/";
      } else {
        console.error("Erreur lors de la déconnexion");
      }
    } catch (err) {
      console.error("Erreur serveur lors de la déconnexion:", err);
    }
  };

  const doughnutData = {
    labels: categories.map((c) => c.name),
    datasets: [
      {
        label: "Dépenses par catégorie",
        data: categories.map((cat) =>
          transactions
            .filter((t) => t.categories.some((c) => c.id === cat.id))
            .reduce((sum, t) => sum + t.amount, 0)
        ),
        backgroundColor: [
          "rgba(127, 255, 212, 0.7)",
          "rgba(255, 165, 0, 0.7)",
          "rgba(255, 99, 132, 0.7)",
          "rgba(54, 162, 235, 0.7)",
          "rgba(75, 192, 192, 0.7)",
          "rgba(153, 102, 255, 0.7)",
          "rgba(255, 206, 86, 0.7)",
        ],
        borderColor: "rgba(255,255,255,0.2)",
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="dashboard">
      <h1>Finans Pamw</h1>
      <h2>Ann nou jere lajan n!</h2>

      <div className="image-container">
        <img src="https://pngimg.com/uploads/piggy_bank/piggy_bank_PNG42.png" alt="Piggy Bank" />
        <img src="https://pngimg.com/uploads/money/money_PNG3518.png" alt="Money" />
      </div>

      <div className="dashboard-links">
        <Link to="/transactions" className="dashboard-btn">Transactions</Link>
        <Link to="/categories" className="dashboard-btn">Catégories</Link>
        <Link to="/budget" className="dashboard-btn">Budgets</Link>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Rechercher une transaction..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Rechercher</button>
      </div>

      <button className="logout-btn" onClick={handleLogout}>LOGOUT</button>

      <div className="dashboard-graph">
        <h3>Dépenses par catégorie</h3>
        <Doughnut data={doughnutData} />
      </div>

      <div className="dashboard-recent">
        <h3>Transactions récentes</h3>
        <ul>
          {filteredTransactions.map((t) => (
            <li key={t.id}>
              {t.description} - {t.amount} HTG -{" "}
              {new Date(t.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
