import React, { useContext, useState } from "react";
import { AppContext } from "../context/AppContext";
import axios from "axios";
import "../App.css";

function Transactions() {
  const {
    transactions,
    setTransactions,
    categories,
    token,
    addTransaction,
    deleteTransaction,
  } = useContext(AppContext);

  const [newTransaction, setNewTransaction] = useState({
    description: "",
    amount: "",
    type: "",
    category_ids: [],
    date: "",
  });

  // ðŸ”§ Gestion des champs du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: name === "amount" ? parseFloat(value) : value,
    }));
  };

  // ðŸ”§ Gestion du choix de catÃ©gorie
  const handleCategoryChange = (e) => {
    const id = parseInt(e.target.value);
    setNewTransaction((prev) => ({
      ...prev,
      category_ids: id ? [id] : [],
    }));
  };

  // ðŸ”§ Soumission du formulaire (via axios)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!token) {
      alert("Token manquant. Veuillez vous reconnecter.");
      return;
    }

    const payload = {
      amount: parseFloat(newTransaction.amount),
      type: newTransaction.type,
      description: newTransaction.description,
      date: newTransaction.date || null,
      category_ids: newTransaction.category_ids,
    };

    try {
      const response = await axios.post(
        "http://localhost:5000/transactions/",
        payload,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const t = {
        ...response.data.transaction,
        categories: Array.isArray(response.data.transaction.categories)
          ? response.data.transaction.categories
          : [],
      };

      setTransactions((prev) => [...prev, t]);
      setNewTransaction({
        description: "",
        amount: "",
        type: "",
        category_ids: [],
        date: "",
      });
    } catch (err) {
      console.error("Erreur lors de lâ€™ajout :", err);
      alert("Erreur lors de lâ€™ajout de la transaction.");
    }
  };

  // ðŸ”§ Suppression dâ€™une transaction
  const handleDelete = async (id) => {
    if (!token) return;
    try {
      await axios.delete(`http://localhost:5000/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTransactions((prev) => prev.filter((t) => t.id !== id));
    } catch (err) {
      console.error("Erreur suppression :", err);
      alert("Impossible de supprimer la transaction.");
    }
  };

  // ðŸ”§ Si pas encore de transactions
  if (!transactions) {
    return <p>Chargement des transactions...</p>;
  }

  return (
    <div className="page-container">
      <h1>Transactions</h1>

      {/* ðŸ§¾ Formulaire dâ€™ajout */}
      <form onSubmit={handleSubmit} className="form-section">
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTransaction.description}
          onChange={handleChange}
          required
        />

        <input
          type="number"
          name="amount"
          placeholder="Montant (HTG)"
          min="0"
          step="0.01"
          value={newTransaction.amount}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="type"
          placeholder="Type (revenu / dÃ©pense)"
          value={newTransaction.type}
          onChange={handleChange}
          required
        />

        <select
          name="category"
          value={newTransaction.category_ids[0] || ""}
          onChange={handleCategoryChange}
        >
          <option value="">-- Choisir une catÃ©gorie --</option>
          {categories.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>

        <input
          type="date"
          name="date"
          value={newTransaction.date}
          onChange={handleChange}
          required
        />

        <button type="submit">Ajouter</button>
      </form>

      {/* ðŸ“‹ Liste des transactions */}
      <div className="list-section">
        {transactions.length === 0 ? (
          <p>Aucune transaction enregistrÃ©e.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Description</th>
                <th>Montant (HTG)</th>
                <th>Type</th>
                <th>CatÃ©gories</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((t) => (
                <tr key={t.id}>
                  <td>{t.description}</td>
                  <td>{t.amount}</td>
                  <td>{t.type}</td>
                  <td>{t.categories.map((c) => c.name).join(", ") || "Aucune"}</td>
                  <td>
                    {t.date ? new Date(t.date).toLocaleDateString() : "â€”"}
                  </td>
                  <td>
                    <button
                      onClick={() => handleDelete(t.id)}
                      className="delete-btn"
                    >
                      Supprimer
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Transactions;
