// src/context/AppContext.js
import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [budget, setBudget] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");


  const token = localStorage.getItem("token");
  // Charger les données au démarrage


  // Charger les transactions
  useEffect(() => {
    fetch("http://localhost:5000/transactions/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        const sanitized = (data.transactions || []).map((t) => ({
          ...t,
          categories: Array.isArray(t.categories) ? t.categories : [],
        }));
        setTransactions(sanitized);
      })
      .catch((err) => console.error("Erreur lors du chargement des transactions :", err));
  }, [token]);




  const fetchData = async () => {
    try {
      const transResa = await axios.get("http://127.0.0.1:5000/transactions");
      const catRes = await axios.get("http://127.0.0.1:5000/categories");
      const budgetRes = await axios.get("http://127.0.0.1:5000/budget");

      setTransactions(transResa.data);
      setCategories(catRes.data);
      setBudget(budgetRes.data.totalBudget);
    } catch (error) {
      setErrorMessage("Erreur lors du chargement des données.");
      console.error(error);
    }
  };

  // Ajouter une transaction
  const addTransaction = async (transaction) => {
    if (!transaction.amount || transaction.amount <= 0) {
      setErrorMessage("Montant invalide.");
      return;
    }

    try {
      const res = await axios.post("/api/transactions", transaction);
      setTransactions([...transactions, res.data]);
      setSuccessMessage("Transaction ajoutée !");
    } catch (error) {
      setErrorMessage("Impossible d'ajouter la transaction.");
      console.error(error);
    }
  };

  // Supprimer une transaction
  const deleteTransaction = async (id) => {
    try {
      await axios.delete(`/api/transactions/${id}`);
      setTransactions(transactions.filter((t) => t.id !== id));
      setSuccessMessage("Transaction supprimée !");
    } catch (error) {
      setErrorMessage("Impossible de supprimer la transaction.");
      console.error(error);
    }
  };

  return (
    <AppContext.Provider
      value={{
        transactions,
        categories,
        budget,
        errorMessage,
        successMessage,
        setErrorMessage,
        setSuccessMessage,
        addTransaction,
        deleteTransaction,
        fetchData,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};
