import React, { useEffect, useState } from "react";
import "../App.css";
import "./Transactions.css";

function Transactions() {
  const [trans, setTrans] = useState([]);
  const [categoriesList, setCategoriesList] = useState([]);
  const [newTransaction, setNewTransaction] = useState({
    description: "",
    amount: "",
    type: "",
    category_ids: [],
    date: "",
  });

  // Charger les transactions
  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:5000/transactions/", {
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => setTrans(data.transactions))
      .catch((err) => console.error("Erreur lors du chargement :", err));
  }, []);

  // Charger les catégories
  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:5000/categories/", {
      headers: { "Authorization": `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setCategoriesList(data.categories))
      .catch((err) => console.error("Erreur chargement catégories :", err));
  }, []);

  // Gérer le formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: name === "amount" ? parseFloat(value) : value,
    }));
  };

  // Gérer le select multiple des catégories
  const handleCategoryChange = (e) => {
    const options = Array.from(e.target.selectedOptions);
    setNewTransaction((prev) => ({
      ...prev,
      category_ids: options.map((o) => parseInt(o.value)),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const transactionToSend = {
      amount: parseFloat(newTransaction.amount),
      type: newTransaction.type,
      description: newTransaction.description,
      date: newTransaction.date ? new Date(newTransaction.date) : new Date(),
      category_ids: newTransaction.category_ids,
    };

    try {
      const response = await fetch("http://localhost:5000/transactions/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(transactionToSend),
      });

      const data = await response.json();

      if (response.ok) {
        setTrans((prev) => [...prev, data.transaction]);
        setNewTransaction({
          description: "",
          amount: "",
          type: "",
          category_ids: [],
          date: "",
        });
      } else {
        console.error("Erreur serveur :", data.message);
      }
    } catch (err) {
      console.error("Erreur fetch :", err);
    }
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(`http://localhost:5000/transactions/${id}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        setTrans((prev) => prev.filter((t) => t.id !== id));
      } else {
        const data = await response.json();
        console.error("Erreur lors de la suppression :", data.message || response.statusText);
      }
    } catch (err) {
      console.error("Erreur réseau :", err);
    }
  };

  return (
    <div className="page-container">
      <h1>Transactions</h1>

      {/* Formulaire d’ajout */}
      <form onSubmit={handleSubmit} className="form-section">
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTransaction.description}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="amount"
          placeholder="Montant"
          min="0"
          step="0.01"
          value={newTransaction.amount}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="type"
          placeholder="Type (revenu / dépense)"
          value={newTransaction.type}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="date"
          value={newTransaction.date}
          onChange={handleChange}
          required
        />

        {/* Select multiple catégories */}
        <select
          multiple
          value={newTransaction.category_ids}
          onChange={handleCategoryChange}
        >
          {categoriesList.map((cat) => (
            <option key={cat.id} value={cat.id}>
              {cat.name}
            </option>
          ))}
        </select>

        <button type="submit">Ajouter</button>
      </form>

      {/* Liste des transactions */}
      <div className="list-section">
        {!trans || trans.length === 0 ? (
          <p>Aucune transaction enregistrée.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Description</th>
                <th>Montant (HTG)</th>
                <th>Catégorie</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {trans.map((t) => (
                <tr key={t.id}>
                  <td>{t.description}</td>
                  <td>{t.amount}</td>
                  <td>{t.categories?.map((c) => c.name).join(", ")}</td>
                  <td>{new Date(t.date).toLocaleDateString()}</td>
                  <td>
                    <button onClick={() => handleDelete(t.id)} className="delete-btn">
                      Supprimer
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Transactions;
