import React, { useContext, useEffect, useState } from "react";
import { AppContext } from "../context/AppContext";
import "../App.css";
import "./Transactions.css";

function Transactions() {
  const { transactions, setTransactions } = useContext(AppContext);
  const [trans, setTrans] = useState([]);
  const [newTransaction, setNewTransaction] = useState({
    description: "",
    amount: "",
    type: "",
    category_ids: [],
    date: "",
  });

  // Charger les transactions au montage
  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:5000/transactions/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setTrans(data.transactions);
        setTransactions(data.transactions);
      })
      .catch((err) => console.error("Erreur lors du chargement :", err));
  }, []);

  // Gestion des changements dans le formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction((prev) => ({
      ...prev,
      [name]: name === "amount" ? parseFloat(value) : value,
    }));
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    // On suppose que 'category' est un ID pour simplifier
    const category_ids = newTransaction.category_ids.length
      ? newTransaction.category_ids
      : newTransaction.category
      ? [parseInt(newTransaction.category)]
      : [];

    const transactionToSend = {
      amount: newTransaction.amount,
      type: newTransaction.type, // ex: "revenu" ou "dépense"
      description: newTransaction.description,
      date: newTransaction.date,
      category_ids,
    };

    try {
      const response = await fetch("http://localhost:5000/transactions/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(transactionToSend),
      });

      const data = await response.json();
      if (response.ok) {
        // ✅ Mettre à jour le state immédiatement
        setTrans((prev) => [...prev, data.transaction]);
        setTransactions((prev) => [...prev, data.transaction]);

        // Reset du formulaire
        setNewTransaction({
          description: "",
          amount: "",
          type: "",
          category_ids: [],
          date: "",
          category: "",
        });
      } else {
        console.error("Erreur serveur :", data.message);
      }
    } catch (err) {
      console.error("Erreur fetch :", err);
    }
  };

  // Suppression d’une transaction
  const handleDelete = async (id) => {
    const token = localStorage.getItem("token");
    try {
      const response = await fetch(`http://localhost:5000/transactions/${id}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      if (response.ok) {
        setTrans((prev) => prev.filter((t) => t.id !== id));
        setTransactions((prev) => prev.filter((t) => t.id !== id));
      } else {
        const data = await response.json();
        console.error("Erreur lors de la suppression :", data.message || response.statusText);
      }
    } catch (err) {
      console.error("Erreur réseau :", err);
    }
  };

  return (
    <div className="page-container">
      <h1>Transactions</h1>

      {/* Formulaire d’ajout */}
      <form onSubmit={handleSubmit} className="form-section">
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTransaction.description}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="amount"
          placeholder="Montant"
          min="0"
          step="0.01"
          value={newTransaction.amount}
          onChange={handleChange}
          required
        />
        <select
          name="type"
          value={newTransaction.type}
          onChange={handleChange}
          required
        >
          <option value="">-- Type --</option>
          <option value="revenu">Revenu</option>
          <option value="dépense">Dépense</option>
        </select>
        <input
          type="text"
          name="category"
          placeholder="ID catégorie"
          value={newTransaction.category}
          onChange={handleChange}
        />
        <input
          type="date"
          name="date"
          value={newTransaction.date}
          onChange={handleChange}
        />
        <button type="submit">Ajouter</button>
      </form>

      {/* Liste des transactions */}
      <div className="list-section">
        {!trans || trans.length === 0 ? (
          <p>Aucune transaction enregistrée.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Description</th>
                <th>Montant (HTG)</th>
                <th>Type</th>
                <th>Catégories</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {trans.map((t) => (
                <tr key={t.id}>
                  <td>{t.description}</td>
                  <td>{t.amount}</td>
                  <td>{t.type}</td>
                  <td>{t.categories.map((c) => c.name).join(", ")}</td>
                  <td>{t.date}</td>
                  <td>
                    <button
                      onClick={() => handleDelete(t.id)}
                      className="delete-btn"
                    >
                      Supprimer
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Transactions;
