import React, { useContext, useEffect, useState } from "react";
import { AppContext } from "../context/AppContext";
import { Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
import "../App.css";

ChartJS.register(ArcElement, Tooltip, Legend);

const Dashboard = () => {
  const { categories } = useContext(AppContext);
  const [transactions, setTransactions] = useState([]); // toutes les transactions
  const [filteredTransactions, setFilteredTransactions] = useState([]); // pour la recherche
  const [searchTerm, setSearchTerm] = useState("");

  // Charger les 3 dernières transactions au montage
  useEffect(() => {
    const token = localStorage.getItem("token");

    fetch("http://localhost:5000/transactions/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.transactions) {
          const sorted = [...data.transactions].sort(
            (a, b) => new Date(b.date) - new Date(a.date)
          );
          const latest = sorted.slice(0, 3); // 3 dernières
          setTransactions(latest);
          setFilteredTransactions(latest);
        }
      })
      .catch((err) => console.error("Erreur chargement transactions :", err));
  }, []);

  // Recherche
  const handleSearch = () => {
    const filtered = transactions.filter((t) =>
      t.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredTransactions(filtered);
  };

  // Déconnexion
  const handleLogout = async () => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch("http://localhost:5000/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        credentials: "include",
      });

      if (response.ok) {
        localStorage.removeItem("token");
        window.location.href = "/";
      } else {
        const data = await response.json();
        console.error("Erreur lors de la déconnexion:", data.message);
      }
    } catch (err) {
      console.error("Erreur serveur lors de la déconnexion:", err);
    }
  };


  return (
    <div className="dashboard">
      <h1>Finans Pamw</h1>
      <h2>Ann nou jere lajan n!</h2>
      <h4>Bienvenue  !!!</h4>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Rechercher une transaction..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Rechercher</button>
      </div>

      <div className="logout-wrapper">
        <button className="logout-btn" onClick={handleLogout}>
          LOGOUT
        </button>
      </div>

      <div className="dashboard-graph">
        <h3>Dépenses par catégorie</h3>
        <Doughnut data={data} />
      </div>

      <div className="dashboard-recent">
        <h3>Transactions récentes</h3>
        <ul>
          {filteredTransactions.map((t) => (
            <li key={t.id}>
              {t.description} - {t.amount} HTG -{" "}
              {new Date(t.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
